// 1. Puxa labels da conversa
const labelsChatwoot = $('webhookDataChatwoot').first().json.body.conversation.labels || [];

// 2. Puxa interesses e servi√ßos do node ‚ÄúSwitch‚Äù, dentro de output
const interesses = $('dataClassifier').first().json.output.interesses || [];
const servicos   = $('dataClassifier').first().json.output.servicos    || [];

// 3. Junta tudo num √∫nico array
const arrayPrincipal = [
  ...labelsChatwoot,
  ...interesses,
  ...servicos,
];

// 4. Define o que deve ser exclu√≠do
const arrayExclusao = [
  "entrega_realizada",
  "envio_proposta_ou_link",
  "fechado_aguardando_pagamento",
  "follow_up_negociacao",
  "novo_contato",
  "pagamento_confirmado",
  "pos_venda",
  "validacao_qualificacao"
];

// 5. Filtra removendo o que est√° em arrayExclusao
let filtrado = arrayPrincipal.filter(item => !arrayExclusao.includes(item));

// 6. Adiciona a etapa atual do funil
filtrado.push(
  $('dataClassifier').first().json.output.etapa_funil
    .toLowerCase()          // deixa tudo em min√∫sculo
    .normalize('NFD')       // separa letras dos acentos
    .replace(/\p{M}/gu, '') // remove todos os acentos, deixando s√≥ as letras
);

// 7. Garante valores √∫nicos
filtrado = Array.from(new Set(filtrado));

// 8. Grava de volta em labels
$json.labels = filtrado;

return $input.item;

